from turtle import Turtle, Screenscreen = Screen()NEW_POSITION = [(0, 0), (-20, 0), (-40, 0)]MOVE_DISTANCE = 20LEFT = 180RIGHT = 0UP = 90DOWN = 270class Snake:    def __init__(self):        self.turtles = []        self.create_snake()        self.head = self.turtles[0]    def create_snake(self):        for turtle_index in NEW_POSITION:            self.add_segments(turtle_index)            self.turtles[0].shape("triangle")    def add_segments(self, position):        new_turtle = Turtle(shape="square")        new_turtle.penup()        new_turtle.goto(position)        new_turtle.color("white")        self.turtles.append(new_turtle)    def extend(self):        self.add_segments(self.turtles[-1].position())    def reset(self):        for seg in self.turtles:            seg.goto(1000, 1000)        self.turtles.clear()        self.create_snake()        self.head = self.turtles[0]    def move(self):        for seg_num in range(len(self.turtles) - 1, 0, -1):            new_x = self.turtles[seg_num - 1].xcor()            new_y = self.turtles[seg_num - 1].ycor()            self.turtles[seg_num].goto(new_x, new_y)        self.head.forward(MOVE_DISTANCE)    def up(self):        if self.head.heading() != DOWN:            self.head.setheading(UP)    def down(self):        if self.head.heading() != UP:            self.head.setheading(DOWN)    def left(self):        if self.head.heading() != RIGHT:            self.head.setheading(LEFT)    def right(self):        if self.head.heading() != LEFT:            self.head.setheading(RIGHT)